import React, { useState, useEffect } from 'react'
import Timer from './components/Timer.js'

export const useEffectCleanup = () => {
  const [showTimer, setShowTimer] = useState(false);
  return (
    <div>
      {showTimer && <Timer />}
      <button onClick={() => setShowTimer(!showTimer)}>
        Toggle
      </button>
    </div>
  )
}

